var foodLog = foodLog || {};

(function () {
  'use strict';

  // Food List Collection
  // A collection of Food Items

  var FoodList = Backbone.Collection.extend({
    model: foodLog.foodItem

  });

  // Create global collection of food items.
  foodLog.foodList = new FoodList();
})();
var foodLog = foodLog || {};

(function () {
  'use strict';

  // Food Item Model
  // Represents one entry in a possible list on entries on one day.

  foodLog.FoodItem = Backbone.Model.extend({
    defaults: {
      title: ''
    }
  });

})();
var foodLog = foodLog || {};

(function () {
  'use strict';

  foodLog.FoodItemView = Backbone.View.extend({

    tagName: 'li',

    template: _.template( $('#foodItem-template').html() ),

    initialize: function () {
      this.listenTo(this.model, 'change', this.render);
      this.render();
    },

    render: function () {
      this.$el.html( this.template( this.model.toJSON() ) );
      $('#week').append(this.el);
    }
  });

})();
var foodLog = foodLog || {};

(function ($) {
  'use strict';

  foodLog.foodLogView = Backbone.View.extend({

    initialize: function () {
      this.listenTo(foodLog.foodList, 'add', this.addOne);
    },

    addOne: function (food) {
      var view = new foodLog.FoodItemView({
        model: new foodLog.FoodItem(food) 
      });
      view.render();
    }

  });

})(jQuery);
